def.body:
output_values = @activation_nodes.last
error = 0.0
@expected_outputs.each_with_index do |_elem, output_index|
  error = error + (0.5 * ((output_values[output_index] - @expected_outputs[output_index]) ** 2))
end
@calculated_error_total = error

input_values.each_with_index do |_elem, input_index|
  @activation_nodes.first[input_index] = input_values[input_index]
end
@weights.each_with_index do |_elem, n|
  @structure[n + 1].times do |j|
    sum = 0.0
    @activation_nodes[n].each_with_index do |_elem, i|
      sum = sum + (@activation_nodes[n][i] * @weights[n][i][j])
    end
    @activation_nodes[n + 1][j] = propagation_function.call(sum)
  end
end

init_activation_nodes
init_weights
init_last_changes
@expected_outputs = Array.new(width, 0.0)
@error_distance_history_max = (error_distance_history_max < 0 ? 0 : error_distance_history_max)
@error_distance = 0.0
@error_distance_history = Array.new(0, 0.0)
self

if @error_distance_history_max < 1
  return @error_distance_history = [-1.0]
end
error = 0.0
output_values = @activation_nodes.last
@expected_outputs.map_with_index do |oe, iw|
  error = error + ((oe - output_values[iw]) ** 2)
end
@error_distance = Math.sqrt(error)
if @error_distance_history.size < (@error_distance_history_max - 1)
  @error_distance_history << @calculated_error_total
else
  @error_distance_history.rotate!
  @error_distance_history[-1] = @calculated_error_total
end
@error_distance_history

index_max = @mini_net_set.size - 1
@weight_height_mismatches = Array(Hash(Symbol, Int32)).new
@weight_height_mismatches = @mini_net_set.map_with_index do |net_from, index|
  if index >= index_max
    nil
  else
    net_to = @mini_net_set[index + 1]
    if net_from.width != net_to.height_considering_bias
      {:from_index => index, :to_index => index + 1, :from_width => net_from.width, :to_height_considering_bias => @mini_net_set[index + 1].height_considering_bias, :from_disable_bias => (      net_from.bias_disabled ? 1 : 0), :to_disable_bias => (      @mini_net_set[index + 1].bias_disabled ? 1 : 0)}
    end
  end
end.compact
@weight_height_mismatches.any? ? false : true

@height_considering_bias = @height + (@bias_disabled ? 0 : 1)
@height_indexes = Array.new(@height_considering_bias) do |i|
  i
end
@inputs_given = Array.new(@height_considering_bias, 0.0)
if @bias_disabled
else
  @inputs_given[-1] = 1.0
end
@input_deltas = Array.new(@height_considering_bias, 0.0)
@width_indexes = Array.new(width) do |i|
  i
end
@outputs_guessed = Array.new(width, 0.0)
@outputs_expected = Array.new(width, 0.0)
@output_deltas = Array.new(width, 0.0)
@weights = @height_indexes.map do
  @width_indexes.map do
    (rand * 2) - 1
  end
end
@last_changes = Array.new(@height_considering_bias, Array.new(width, 0.0))
@error_total = 0.0
@error_distance_history_max = (error_distance_history_max < 0 ? 0 : error_distance_history_max)
@error_distance = 0.0
@error_distance_history = Array.new(0, 0.0)

error = 0.0
@outputs_expected.map_with_index do |oe, iw|
  error = error + (0.5 * ((oe - @outputs_guessed[iw]) ** 2))
end
@error_total = error
@error_total

if @error_distance_history_max < 1
  return @error_distance_history = [-1.0]
end
if @error_distance_history.size < (@error_distance_history_max - 1)
  @error_distance_history << @error_total
else
  @error_distance_history.rotate!
  @error_distance_history[-1] = @error_total
end
@error_distance_history
